// Este exemplo gera um tempo de espera aleatório entre 10 e 60 segundos
const minWait = 8; // Tempo mínimo de espera em segundos
const maxWait = 15; // Tempo máximo de espera em segundos
const randomWaitTime = Math.floor(Math.random() * (maxWait - minWait + 1)) + minWait;

return [
  {
    json: {
      waitTime: randomWaitTime
    }
  }
];

